mapper.update:
update pets
set petName     = #{petName},
    petBirthday = #{petBirthday},
    typeId      = #{typeId},
    ownerId     = #{ownerId}
where petId = #{petId}

mapper.select:
select o.ownerName, o.ownerPassword
from owners o
where o.ownerName = #{LoginInfo}
   or o.ownerTelephone = #{LoginInfo}
